/**
 * Calculates the square root of a number using the Babylonian method.
 *
 * @param {number} num The number to calculate the square root of.
 * @param {number} [tolerance=0.0001] The desired accuracy of the result.
 * @returns {number} The approximate square root of the number.
 * @throws {Error} If the input is not a non-negative number.
 */
function squareRoot(num, tolerance = 0.0001) {
  if (typeof num !== 'number' || num < 0) {
    throw new Error("Input must be a non-negative number.");
  }

  if (num === 0) {
    return 0;
  }

  let guess = num; // Initial guess - can be improved for faster convergence
  let nextGuess;

  while (true) {
    nextGuess = 0.5 * (guess + num / guess);
    if (Math.abs(guess - nextGuess) < tolerance) {
      return nextGuess;
    }
    guess = nextGuess;
  }
}

// Example usage:
console.log(squareRoot(9));   // Output: 3.000000001396984
console.log(squareRoot(2));   // Output: 1.4142135623746899
console.log(squareRoot(16));  // Output: 4.000000000000002
console.log(squareRoot(0));   // Output: 0
console.log(squareRoot(100, 0.00001)); // Output: 10.000000000000002

// Example of error handling:
try {
  console.log(squareRoot(-4));
} catch (error) {
  console.error(error.message); // Output: Input must be a non-negative number.
}
